codebase.body:
/* Tokens */
:root{
  --bg:#0b0c1a;--fg:#f5f3ee;--muted:#b8b4ad;
  --card:#151823;--border:rgba(245,243,238,.12);
  --accent:#274690;--amber:#fca311;--r:14px;
  --space:18px;--max:980px;--shadow:0 12px 28px rgba(0,0,0,.35);
}
@media (prefers-color-scheme: light){
  :root{--bg:#eae4e0;--fg:#151823;--muted:#5b5a57;--card:#ffffff;--border:rgba(21,24,35,.12);--shadow:0 10px 22px rgba(0,0,0,.12);}
}

/* Base */
*{box-sizing:border-box} body{margin:0;font:16px/1.5 system-ui;background:var(--bg);color:var(--fg)}
a{color:inherit;text-decoration:none}
.container{max-width:var(--max);margin:0 auto;padding:0 var(--space)}
.muted{color:var(--muted)}

/* Header */
.site-header{position:sticky;top:0;background:color-mix(in oklab,var(--bg) 88%,transparent);backdrop-filter:blur(10px);border-bottom:1px solid var(--border)}
.header-row{display:flex;align-items:center;justify-content:space-between;height:64px}
.brand{font-weight:650;letter-spacing:.2px}
.nav{display:flex;gap:14px} .nav a{padding:8px 10px;border-radius:10px}
.nav a:hover{background:color-mix(in oklab,var(--card) 70%,transparent)}

/* Sections */
.section{padding:42px 0} .section-head{margin-bottom:16px}
.hero{display:grid;grid-template-columns:1.2fr .8fr;gap:24px;padding:46px 0}
.eyebrow{color:var(--amber);font-weight:650;margin:0 0 8px}
.hero h1{font-size:38px;line-height:1.08;margin:0 0 10px}
.hero-art img{width:100%;max-width:220px;display:block;margin-left:auto;filter:drop-shadow(var(--shadow))}

/* Components */
.card{background:var(--card);border:1px solid var(--border);border-radius:var(--r);padding:18px;box-shadow:var(--shadow)}
.btn{display:inline-flex;align-items:center;justify-content:center;padding:10px 14px;border-radius:12px;background:linear-gradient(90deg,var(--accent),var(--amber));border:0;color:white;font-weight:650}
.btn-ghost{background:transparent;border:1px solid var(--border);color:var(--fg)}
.cta-row{display:flex;gap:10px;margin-top:12px}
.link{color:color-mix(in oklab,var(--amber) 70%,var(--fg));font-weight:600}

.projects-grid{display:grid;grid-template-columns:repeat(3,1fr);gap:14px}
.stack{display:flex;flex-direction:column;gap:10px}
.form{display:flex;flex-direction:column;gap:12px}
.field{display:flex;flex-direction:column;gap:6px}
input,textarea{background:transparent;border:1px solid var(--border);border-radius:12px;color:var(--fg);padding:10px 12px;font:inherit}

.footer{padding:28px 0;border-top:1px solid var(--border)}
.footer-row{display:flex;align-items:center;justify-content:space-between}

@media (max-width: 780px){
  .hero{grid-template-columns:1fr}
  .projects-grid{grid-template-columns:1fr}
}

